//let timeframe = 2h;
let portrange = dynamic(["21", "22", "23", "25", "53", "110", "135", "137", "138", "139", "1433", "1434"]);
CommonSecurityLog
//| where TimeGenerated >= ago(timeframe)
| where DeviceAction == "Allow" and DestinationPort in (portrange)
| where not(ipv4_is_match("10.0.0.0", DestinationIP, 8) or ipv4_is_match("172.16.0.0", DestinationIP, 12) or ipv4_is_match("192.168.0.0", DestinationIP, 16))
| where (ipv4_is_match("10.0.0.0", SourceIP, 8) or ipv4_is_match("172.16.0.0", SourceIP, 12) or ipv4_is_match("192.168.0.0", SourceIP, 16))
| summarize
    DestPortCount=dcount(DestinationPort),
    start_time= min(TimeGenerated),
    end_time= max(TimeGenerated), Firewall=make_set(Computer)
    by SourceIP, DestinationIP, DestinationPort
| extend IPCustomEntity = DestinationIP

//database 1433, 4022, 135, 1434, 1434
//ftp 20,21
//IRC 194 , 6667
//dap 389,636,3268,3269
//ssh 22,23,25,53
let AllPorts = dynamic(["1433", "4022", "135", "1434", "1434", "20", "21", "194", "6667", "389", "636", "3268", "3269", "22", "23", "25", "53", "110", "123", "119"]);
CommonSecurityLog
//|where TimeGenerated >= ago(1h)
| where (ipv4_is_match("10.0.0.0", SourceIP, 8) or ipv4_is_match("172.16.0.0", SourceIP, 12) or ipv4_is_match("192.168.0.0", SourceIP, 16) or ipv4_is_match("192.0.0.0", SourceIP, 16))
| where (ipv4_is_match("10.0.0.0", DestinationIP, 8) or ipv4_is_match("172.16.0.0", DestinationIP, 12) or ipv4_is_match("192.168.0.0", DestinationIP, 16) or ipv4_is_match("192.0.0.0", DestinationIP, 16))
| where DestinationPort in (AllPorts)
| where DeviceAction == "allow" or DeviceAction == "accept"
| summarize
    start_time= min(TimeGenerated),
    end_time= max(TimeGenerated),
    total_count= dcount(DestinationIP),
    evtcount = count()
    by SourceIP, bin(TimeGenerated, 10m)
| where total_count >= 50
| where evtcount >= 5

//database 1433, 4022, 135, 1434, 1434
//ftp 20,21
//IRC 194 , 6667
//dap 389,636,3268,3269
//ssh 22,23,25,53
//rpc 135
//windows server scanner 110,123,119
let AllPorts = dynamic(["1433","4022","135","1434","20","21","194","6667","389","636","3268","3269","22","23","25","110","123","119"]);
CommonSecurityLog
//|where TimeGenerated >= ago(48h)
| where (ipv4_is_match("10.0.0.0", SourceIP, 8) or ipv4_is_match("172.16.0.0",SourceIP, 12) or ipv4_is_match("192.168.0.0", SourceIP, 16) or ipv4_is_match("192.0.0.0", SourceIP, 16))
| where not((ipv4_is_match("10.0.0.0", DestinationIP, 8) or ipv4_is_match("172.16.0.0",DestinationIP, 12) or ipv4_is_match("192.168.0.0", DestinationIP, 16) or ipv4_is_match("192.0.0.0", DestinationIP, 16)))
| where DestinationPort in (AllPorts)
| summarize start_time= min(TimeGenerated), end_time= max(TimeGenerated),total_count= dcount(DestinationIP),evtcount = count() by SourceIP, bin(TimeGenerated,10m), DestinationPort
| where total_count >= 59
| where evtcount >= 5

//windows 110,123,119
//rpc 135
//database 1433, 4022, 135, 1434, 1433, 1434, 3306, 66, 1521, 1525, 1526, 1527, 1528, 1529, 1571, 1575, 1630, 1748, 1754, 1808, 1809, 2481, 2482, 2484, 3872, 3891, 3938, 9088, 5432, 50000
//ftp 20,21
//IRC 194 , 6667
//dap 389,636,3268,3269
//ssh 22,23,25,53
let commonPorts= dynamic([80, 443, 20, 21, 22, 23, 25, 3389, 1521, 3306, 5432, 53, 1433, 137, 138, 139, 445, 110, 123, 119, 135, 4022, 1434, 194, 6667, 389, 636, 3268, 3269, 1433, 1434, 3306, 66, 1521, 1525, 1526, 1527, 1528, 1529, 1571, 1575, 1630, 1748, 1754, 1808, 1809, 2481, 2482, 2484, 3872, 3891, 3938, 9088, 5432, 50000]);
CommonSecurityLog
//| where TimeGenerated >= ago(1h)
| where not(ipv4_is_match("10.0.0.0", SourceIP, 8) or ipv4_is_match("172.16.0.0", SourceIP, 12) or ipv4_is_match("192.168.0.0", SourceIP, 16) or ipv4_is_match("192.0.0.0", SourceIP, 16))
| where DestinationPort in (commonPorts)
| where DeviceAction !contains "deny"
| where Activity !contains "deny"
| summarize
    start_time= min(TimeGenerated),
    end_time= max(TimeGenerated),
    Total_count_of_DestinationIP= dcount(DestinationIP),
    DestinationIPSet=make_set(DestinationIP),
    evtcount = count()
    by SourceIP, DestinationPort, DeviceAction, Activity, bin(TimeGenerated, 10m)
| where Total_count_of_DestinationIP >= 25
| where evtcount >= 5

let watchlist = (_GetWatchlist('Stone_Panda_IOC'));
CommonSecurityLog
| where DestinationIP in (watchlist)
